import { motion } from "framer-motion"
import React, { useRef, useState } from "react"

const HeaderMenu = ({ children }) => {
    return (
        <div
            style={{
                margin: '22px 0px'
            }}
        >
            {children}
        </div>
    )
}

interface ItemProps {
    children: React.ReactElement
    popup?: React.ReactElement
}

const Item = (props: ItemProps) => {
    const { children, popup } = props
    const [popupVisible, setPopupVisible] = useState(false)
    const itemRef = useRef()
    return (
        <>
            <motion.button
                ref={itemRef}
                whileTap={{
                    scale: 0.9
                }}
                style={{
                    fontSize: 22,
                    margin: 8,
                    cursor: 'pointer',
                    // backgroundColor: 'transparent',
                    backgroundColor: 'blue',
                    border: 'none',
                    outline: 'none'
                }}
                onClick={() => {
                    setPopupVisible(true)
                    console.log(itemRef.current)
                    console.log(itemRef.current.offsetTop)
                    console.log(itemRef.current.offsetLeft)
                    console.log('popup', popup.offseTop)
                }}
                onBlur={() => {
                    // alert('blue')
                    // setPopupVisible(false)
                }}
            >
                {children}
            </motion.button>
            {popup && popupVisible && React.cloneElement(popup, {
                style: {
                    backgroundColor: 'red',
                    position: 'absolute',
                    top: itemRef.current.offsetTop + itemRef.current.offsetHeight,
                    left: itemRef.current.offsetLeft
                }
            })}
        </>
    )
}

export {
    Item
}

export default HeaderMenu
