import CardBook from '../components/CardBook'
import Layout from '../components/Layout'
import Waterfall from '../components/Waterfall'
// import Dialog from 'rc-dialog'
import Dialog from '../components/Dialog'
import Comment from '../components/Comment'
import { useState } from 'react'

const App = () => {
  const [visible, setVisible] = useState(false)
  return (
    <Layout>
      <Dialog
        visible={visible}
        // title='这脑洞简直了，纸张艺术啊！❤️'
        // image="//ci.xiaohongshu.com/a65dec9a-99ae-41e6-af72-d259e9571eaa?imageView2/2/w/1080/format/jpg"
        onClose={() => {
          setVisible(false)
        }}
      >
        <div
          style={{
            display: 'flex'
          }}
        >
          <img
              width={500}
              height={500}
              src='//ci.xiaohongshu.com/a65dec9a-99ae-41e6-af72-d259e9571eaa?imageView2/2/w/1080/format/jpg'
              style={{
                  verticalAlign: 'top',
                  objectFit: 'cover'
              }}
          />
          {/* comment box */}
          <div className='comment-wrapper'>
            {/* one comment */}
            {Array(10).fill(0).map(comment => (
              <Comment
                text='哈咯大家好～今天是2021年的第二天！新年新气象，昨天是新年第一天，我在写新年目标和展望的时候，想把事业这一块的思考分享给大家，这也是我从大学时期就想明白的一个事情，随着时间的推移也在慢慢调整。欢迎大家对号入座，希望对大家能有所启发。'
              />
            ))}
          </div>
        </div>
      </Dialog>
      <Waterfall
        renderItem={item => (
          <CardBook
            onClick={() => {
              // alert(99)
              setVisible(true)
            }}
            post={item}/>
        )}
        dataSource={[
            {
              title: 'title',
              image: '//aa',
              type: 'image',
              user: {
                  username: 'username',
                  avatar: '//aa'
              },
              count: {
                  likes: 1
              }
          },
          {
            title: 'title',
            image: '//aa',
            type: 'image',
            user: {
                username: 'username',
                avatar: '//aa'
            },
            count: {
                likes: 1
            }
          }
        ]}
      />
    </Layout>
  )
}

export default App
