import CardBook from '../components/CardBook'
import Layout from '../components/Layout'
import Waterfall from '../components/Waterfall'
// import Dialog from 'rc-dialog'
import Dialog from '../components/Dialog'
import Comment from '../components/Comment'
import { useState } from 'react'

const App = () => {
  const [visible, setVisible] = useState(false)
  return (
    <Layout>
      <Dialog
        visible={visible}
        // title='这脑洞简直了，纸张艺术啊！❤️'
        // image="//ci.xiaohongshu.com/a65dec9a-99ae-41e6-af72-d259e9571eaa?imageView2/2/w/1080/format/jpg"
        onClose={() => {
          setVisible(false)
        }}
      >
        <div
          style={{
            display: 'flex'
          }}
        >
          <img
              width={500}
              height={500}
              src='//ci.xiaohongshu.com/a65dec9a-99ae-41e6-af72-d259e9571eaa?imageView2/2/w/1080/format/jpg'
              style={{
                  verticalAlign: 'top',
                  objectFit: 'cover'
              }}
          />
          {/* comment box */}
          <div style={{
            margin: 12,
            flex: 1,
            // backgroundColor: 'red',
            // height: 200,
            height: 476,
            overflowY: 'auto'
          }}>
            {/* one comment */}
            {Array(10).fill(0).map(comment => (
              <Comment />
            ))}
          </div>
        </div>
      </Dialog>
      <Waterfall
        renderItem={item => (
          <CardBook
            onClick={() => {
              // alert(99)
              setVisible(true)
            }}
            post={item}/>
        )}
        dataSource={[
            {
              title: 'title',
              image: '//aa',
              type: 'image',
              user: {
                  username: 'username',
                  avatar: '//aa'
              },
              count: {
                  likes: 1
              }
          },
          {
            title: 'title',
            image: '//aa',
            type: 'image',
            user: {
                username: 'username',
                avatar: '//aa'
            },
            count: {
                likes: 1
            }
          }
        ]}
      />
    </Layout>
  )
}

export default App
