import CardBook from '../components/CardBook'
import Layout from '../components/Layout'
import Waterfall from '../components/Waterfall'
// import Dialog from 'rc-dialog'
import PostDialog from '../components/PostDialog'
import CommentBox from '../components/CommentBox'
import Skeleton from '../components/Skeleton'
import { useState } from 'react'
import store from '../store'
import { inject, Provider } from 'mobx-react'

const App = (props) => {
  const { store } = props
  const { PostDialogStore } = store
  const [visible, setVisible] = useState(false)
  return (
    <Layout>
      {/* <PostDialog
        // visible={PostDialogStore.visible}
        // visible={true}
      /> */}

      {JSON.stringify(PostDialogStore.visible)}

      <Waterfall
        renderItem={item => (
          <CardBook
            onClick={() => {
              PostDialogStore.open()
              // setVisible(true)
              // console.log('__next', document.querySelector('#__next').style)
              // document.querySelector('#__next').style.filter = 'blur(4px)'
            }}
            post={item}/>
        )}
        dataSource={[
            {
              title: 'title',
              image: '//aa',
              type: 'image',
              user: {
                  username: 'username',
                  avatar: '//aa'
              },
              count: {
                  likes: 1
              }
          },
          {
            title: 'title',
            image: '//aa',
            type: 'image',
            user: {
                username: 'username',
                avatar: '//aa'
            },
            count: {
                likes: 1
            }
          }
        ]}
      />
    </Layout>
  )
}

// export default inject('store')(App)

App = inject('store')(App)

export default (
  <Provider store={store}>
    {App}
  </Provider>
)

