import { useRef } from "react"

interface SekeletonImageProps {
    width: number
    height: number
    src: string
}

const SkeletonImage = (props: SekeletonImageProps) => {
    const { width, height, src } = props
    const imageRef = useRef<HTMLImageElement>()
    const imageBuffer = new Image()
    imageBuffer.src = '//ci.xiaohongshu.com/a65dec9a-99ae-41e6-af72-d259e9571eaa?imageView2/2/w/1080/format/jpg'
    imageBuffer.onload = () => {
        console.log('image loaded', imageRef)
        imageRef.current.src = imageBuffer.src
    }

    return (
        <img
            width={width}
            height={height}
            ref={imageRef}
        />
    )
}

export default SkeletonImage
