const CardBook = (props: WaterfallCardProps) => {
    const { store, onLoad, post } = props
    const { title, image, count, user, type } = post
    const { PostModalStore } = store
    const [ like, setLike ] = useState(false)
    const [ collect, setCollect ] = useState(false)
    return (
        <CardWrapper>
            <CardImage
                src={image}
                onClick={() => {
                    console.log('PostModalStore', PostModalStore)
                    PostModalStore.open()
                }}
                onLoad={onLoad}
                type={type}
                showImageIcon={count.images > 1}
            />
            <CardTitle>{title}</CardTitle>
            <CardAction>
                <User>
                    <Link to='/user'>
                        <UserAvatar
                            // src="https://img.xiaohongshu.com/avatar/5f75d88ca8204500012c1379.jpg@240w_240h_90q_1e_1c_1x.jpg"
                            src={user.avatar}
                            />
                    </Link>
                    <Username to='/user'>{user.username}</Username>
                </User>
                <CardActionButton
                    defaultIcon={<AiOutlineHeart />}
                    activeIcon={<AiFillHeart />}
                    value={count.likes}
                    active={like}
                    color='red'
                    onClick={() => {
                        if(like) {
                            setLike(false)
                        } else {
                            setLike(true)
                        }
                    }}
                />
                <CardActionButton
                    defaultIcon={<AiOutlineStar />}
                    activeIcon={<AiFillStar />}
                    color='#FAC35B'
                    value={count.likes}
                    active={collect}
                    onClick={() => {
                        if(collect) {
                            setCollect(false)
                        } else {
                            setCollect(true)
                        }
                    }}
                />
                {/* <CardActionButton
                    defaultIcon={<AiOutlineMessage />}
                    value={23}
                /> */}
            </CardAction>
        </CardWrapper>
    )
}

// export default inject('store')(observer(CardBook))
export default CardBook
