import { useState } from "react"

const Dropdown = ({ children, popup }) => {
    const [popupVisible, setPopupVisible] = useState(false)
    return (
        <>
            {popupVisible ? popup : null}
            {/* {children} */}
            {React.cloneElement(children, {
                onMouseEnter: () => {
                    setPopupVisible(true)
                }
            })}
        </>
    )
}

export default Dropdown
