import { motion } from "framer-motion"
import React, { useRef, useState } from "react"

const POPUP_WIDTH = 100

const HeaderMenu = ({ children }) => {
    return (
        <div
            style={{
                margin: '22px 0px'
            }}
        >
            {children}
        </div>
    )
}

interface ItemProps {
    children: React.ReactElement
    popup?: React.ReactElement
}

const Item = (props: ItemProps) => {
    const { children, popup, popupOffset } = props
    const [popupVisible, setPopupVisible] = useState(false)
    const itemRef = useRef()
    return (
        <>
            <motion.button
                ref={itemRef}
                whileTap={{
                    scale: 0.9
                }}
                style={{
                    fontSize: 22,
                    margin: 8,
                    cursor: 'pointer',
                    backgroundColor: 'transparent',
                    // backgroundColor: 'blue',
                    border: 'none',
                    outline: 'none'
                }}
                onClick={() => {
                    setPopupVisible(true)
                }}
                onBlur={() => {
                    setPopupVisible(false)
                }}
            >
                {children}
            </motion.button>
            {/* {popup && popupVisible && React.cloneElement(popup, {
                style: {
                    backgroundColor: 'red',
                    position: 'absolute',
                    width: POPUP_WIDTH,
                    top: itemRef.current.offsetTop + itemRef.current.offsetHeight + popupOffset[0],
                    left: itemRef.current.offsetLeft - (POPUP_WIDTH - itemRef.current.offsetWidth) + popupOffset[1]
                }
            })} */}
            {popup && popupVisible && (
                <motion.div
                    style={{
                        // backgroundColor: 'red',
                        position: 'absolute',
                        width: POPUP_WIDTH,
                        boxShadow: '0 2px 8px -2px rgba(0,0,0,0.3)',
                        top: itemRef.current.offsetTop + itemRef.current.offsetHeight + popupOffset[0],
                        left: itemRef.current.offsetLeft - (POPUP_WIDTH - itemRef.current.offsetWidth) + popupOffset[1]
                    }}
                    initial={{
                        y: 3
                    }}
                    animate={{
                        y: 0
                    }}
                >
                    {popup}
                </motion.div>
            )}
        </>
    )
}

export {
    Item
}

export default HeaderMenu
