import { inject, observer } from 'mobx-react'
import { useState } from 'react'
import CardBook from '../components/CardBook'
import Layout from '../components/Layout'
// import Dialog from 'rc-dialog'
import PostDialog from '../components/PostDialog'
import Waterfall from '../components/Waterfall'

const App = (props) => {
  const { store } = props
  const { PostDialogStore } = store
  const [visible, setVisible] = useState(false)
  console.log('PostDialogStore', PostDialogStore)
  return (
    <Layout>
      <PostDialog
        visible={PostDialogStore.visible}
        // visible={true}
      />

      <Waterfall
        renderItem={item => (
          <CardBook
            key={item.id}
            onClick={() => {
              PostDialogStore.open()
              // setVisible(true)
              // console.log('__next', document.querySelector('#__next').style)
              // document.querySelector('#__next').style.filter = 'blur(4px)'
            }}
            post={item}/>
        )}
        dataSource={[
            {
              id: 1,
              title: 'title',
              image: '//aa',
              type: 'image',
              user: {
                  username: 'username',
                  avatar: '//aa'
              },
              count: {
                  likes: 1
              }
          },
          {
            id: 2,
            title: 'title',
            image: '//aa',
            type: 'image',
            user: {
                username: 'username',
                avatar: '//aa'
            },
            count: {
                likes: 1
            }
          }
        ]}
      />
    </Layout>
  )
}

export default inject('store')(observer(App))
